89: contract ClockAuctionBase {
90:     struct Auction {
91:         address seller;
92:         uint128 startingPrice;
93:         uint128 endingPrice;
94:         uint64 duration;
95:         uint64 startedAt;
96:         uint64 isGen0;
97:     }
98:     ERC721 public nonFungibleContract;
99:     uint256 public ownerCut;
100:     mapping (uint256 => Auction) tokenIdToAuction;
101:     event AuctionCreated(uint256 tokenId, uint256 startingPrice, uint256 endingPrice, uint256 duration);
102:     event AuctionSuccessful(uint256 tokenId, uint256 totalPrice, address winner);
103:     event AuctionCancelled(uint256 tokenId);
104:     function _owns(address _claimant, uint256 _tokenId) internal view returns (bool) {
105:         return (nonFungibleContract.ownerOf(_tokenId) == _claimant);
106:     }
107:     function _escrow(address _owner, uint256 _tokenId) internal {
108:         nonFungibleContract.transferFrom(_owner, this, _tokenId);
109:     }
110:     function _transfer(address _receiver, uint256 _tokenId) internal {
111:         nonFungibleContract.transfer(_receiver, _tokenId);
112:     }
113:     function _addAuction(uint256 _tokenId, Auction _auction) internal {
114:         require(_auction.duration >= 1 minutes);
115:         tokenIdToAuction[_tokenId] = _auction;
116:         AuctionCreated(
117:             uint256(_tokenId),
118:             uint256(_auction.startingPrice),
119:             uint256(_auction.endingPrice),
120:             uint256(_auction.duration)
121:         );
122:     } 
123:     function _cancelAuction(uint256 _tokenId, address _seller) internal {
124:         _removeAuction(_tokenId);
125:         _transfer(_seller, _tokenId);
126:         AuctionCancelled(_tokenId);
127:     }
128:     function _bid(uint256 _tokenId, uint256 _bidAmount)
129:         internal
130:         returns (uint256)
131:     {
132:         Auction storage auction = tokenIdToAuction[_tokenId];
133:         require(_isOnAuction(auction));
134:         uint256 price = _currentPrice(auction);
135:         require(_bidAmount >= price);
136:         address seller = auction.seller;
137:         _removeAuction(_tokenId);
138:         if (price > 0) {
139:             uint256 auctioneerCut = _computeCut(price);
140:             uint256 sellerProceeds = price - auctioneerCut;
141:             seller.transfer(sellerProceeds);
142:         }
143:         uint256 bidExcess = _bidAmount - price;
144:         msg.sender.transfer(bidExcess);
145:         AuctionSuccessful(_tokenId, price, msg.sender);
146:         return price;
147:     }
148:     function _removeAuction(uint256 _tokenId) internal {
149:         delete tokenIdToAuction[_tokenId];
150:     }
151:     function _isOnAuction(Auction storage _auction) internal view returns (bool) {
152:         return (_auction.startedAt > 0);
153:     }
154:     function _currentPrice(Auction storage _auction)
155:         internal
156:         view
157:         returns (uint256)
158:     {
159:         uint256 secondsPassed = 0;
160:         if (now > _auction.startedAt) {
161:             secondsPassed = now - _auction.startedAt;
162:         }
163:         return _computeCurrentPrice(
164:             _auction.startingPrice,
165:             _auction.endingPrice,
166:             _auction.duration,
167:             secondsPassed
168:         );
169:     }
170:     function _computeCurrentPrice(
171:         uint256 _startingPrice,
172:         uint256 _endingPrice,
173:         uint256 _duration,
174:         uint256 _secondsPassed
175:     )
176:         internal
177:         pure
178:         returns (uint256)
179:     {
180:         if (_secondsPassed >= _duration) {
181:             return _endingPrice;
182:         } else {
183:             int256 totalPriceChange = int256(_endingPrice) - int256(_startingPrice);
184:             int256 currentPriceChange = totalPriceChange * int256(_secondsPassed) / int256(_duration);
185:             int256 currentPrice = int256(_startingPrice) + currentPriceChange;
186:             return uint256(currentPrice);
187:         }
188:     }
189:     function _computeCut(uint256 _price) internal view returns (uint256) {
190:         return _price * ownerCut / 10000;
191:     }
192: }
